function showToast(message,type='success',position='top-right',duration=1000){const validTypes=['success','danger','warning'];if(!validTypes.includes(type)){console.error('Invalid toast type. Use: success, danger, warning');return;}
    const validPositions=['top-right','top-left','bottom-right','bottom-left','top-center','bottom-center'];if(!validPositions.includes(position)){console.error('Invalid position. Use: top-right, top-left, bottom-right, bottom-left, top-center, bottom-center');position='top-right';}
    let container=document.querySelector(`.toast-container.${position}`);if(!container){container=document.createElement('div');container.className=`toast-container ${position}`;document.body.appendChild(container);}
    const toast=document.createElement('div');toast.className=`toast ${type}show`;toast.innerHTML=`<span>${message}</span><button class="close-btn"></button>`;toast.style.animationDuration=`${duration}ms`;container.appendChild(toast);setTimeout(()=>{toast.classList.remove('show');toast.classList.add('hide');setTimeout(()=>toast.remove(),300);},duration);toast.querySelector('.close-btn').addEventListener('click',()=>{toast.classList.remove('show');toast.classList.add('hide');setTimeout(()=>toast.remove(),300);});}